create table if not exists BOOKS (
	id  IDENTITY NOT NULL PRIMARY KEY,
	title VARCHAR(100),
	description VARCHAR(500),
	cover_image VARCHAR(1000),
	price NUMERIC,
	author INT
);


create table users (
	id IDENTITY NOT NULL PRIMARY KEY,
	name VARCHAR(50),
	last_name VARCHAR(50),
	pseudonym VARCHAR(50),
	is_banned INT(1),
	username VARCHAR(15),
	password VARCHAR(100)
);
